                    case "IsNoDiscount":
                        break;
                    case "FixDiscount":
                        if (PriceLevelType.Is(CPC.POS.PriceLevelType.FixedDiscountOnAllItems) && (!FixDiscount.HasValue || FixDiscount <= 0))
                            message = "Fix Discount is required";
                        break;
                    case "Unit":
