                    case "PriceSchemeId":
                        if (PriceLevelType.Is(CPC.POS.PriceLevelType.MarkdownPriceLevel) && (!PriceSchemeId.HasValue || PriceSchemeId == 0))
                            message = "Mark down price level is required";
                        break;
                    case "Custom1":
                        break;
                    case "Custom2":
                        break;
                    case "Custom3":
                        break;
                    case "Custom4":
                        break;
                    case "Custom5":
                        break;
                    case "Custom6":
                        break;
                    case "Custom7":
