                            message = "Layaway No required";
                        break;
                    case "ChargeACNo":
                        if (ChargeACNOFlag && string.IsNullOrWhiteSpace(ChargeACNo))
                            message = "Charge AC No required";
                        break;
                }

                if (!string.IsNullOrWhiteSpace(message))
