        /// <summary>
        /// Gets the LegalName
        /// </summary>
        public string LegalName
        {
            get
            {
                string legalName = string.Empty;
                if (string.IsNullOrWhiteSpace(FirstName))
                    legalName = LastName;
                else if (string.IsNullOrWhiteSpace(LastName))
                    legalName = FirstName;
                else
                    legalName = string.Format("{0}, {1}", LastName, FirstName);
                return legalName;
            }
        }

