            {
                return !string.IsNullOrWhiteSpace(Error);
            }
        }

        #endregion

        #region ResourceString

        /// <summary>
        /// Gets or sets the ResourceString.
        /// </summary>
        public string ResourceString
        {
            get
            {
                if (this.Resource.HasValue)
                    return this.Resource.ToString();
                return Guid.NewGuid().ToString();
                     
            }

        }
        #endregion

        #region TotalReward
        private int _totalReward;
        /// <summary>
        /// Gets or sets the TotalReward.
        /// </summary>
        public int TotalReward
        {
            get { return _totalReward; }
            set
            {
                if (_totalReward != value)
                {
                    _totalReward = value;
                    OnPropertyChanged(() => TotalReward);
                }
            }
        }
        #endregion



        #endregion

        #region Override Methods

        protected override void PropertyChangedCompleted(string propertyName)
        {
            switch (propertyName)
            {
                case "FirstName":
                    OnPropertyChanged(() => LegalName);
                    break;
                case "LastName":
                    OnPropertyChanged(() => LegalName);
                    break;
                case "GuestTypeId":
                    //Using for customer if Customer is Restailer => customer is not reward member
                    if (Mark != null && Mark.Equals(MarkType.Customer.ToDescription()) && GuestTypeId == (short)CustomerTypes.Retailer)
                    {
                        IsRewardMember = false;
                    }
                    break;
            }
        }
        #endregion

        #region Methods

        public void RaiseOnChangedActualHours()
        {
            OnPropertyChanged(() => ActualHours);
        }

        public void RaiseContact()
        {
            OnPropertyChanged(() => FirstName);
            OnPropertyChanged(() => LastName);
            OnPropertyChanged(() => Phone1);
            OnPropertyChanged(() => CellPhone);
            OnPropertyChanged(() => Email);
            OnPropertyChanged(() => IsPrimary);
        }

        #endregion

        #endregion

        #region IDataErrorInfo Members

        protected HashSet<string> _extensionErrors = new HashSet<string>();
        /// <summary>
        /// <para> Gets or sets the ExtensionErrors </para>
        /// </summary>
        public HashSet<string> ExtensionErrors
        {
            get
            {
                return _extensionErrors;
            }
            set
            {
                if (_extensionErrors != value)
                {
                    _extensionErrors = value;
                    OnPropertyChanged(() => ExtensionErrors);
                }
            }
        }

        public string Error
        {
            get
            {
                List<string> errors = new List<string>();
                PropertyDescriptorCollection props = TypeDescriptor.GetProperties(this);
                foreach (PropertyDescriptor prop in props)
                {
                    string msg = this[prop.Name];
                    if (!string.IsNullOrWhiteSpace(msg))
                    {
                        errors.Add(msg);
                    }
                }
                return string.Join(Environment.NewLine, errors);
            }
        }

        public string this[string columnName]
        {
            get
            {
                string message = string.Empty;
                this.ExtensionErrors.Remove(columnName);

                switch (columnName)
                {
                    case "FirstName":
                        if (string.IsNullOrEmpty(FirstName) || (this.FirstName != null && this.FirstName.Trim().Length == 0))
                        {
                            if (Mark == null || !Mark.Equals(MarkType.Vendor.ToDescription()))
                                message = "FirstName is required";
                        }
                        break;
                    case "LastName":
                        if (string.IsNullOrEmpty(LastName) || (this.LastName != null && this.LastName.Trim().Length == 0))
                        {
                            if (Mark == null || !Mark.Equals(MarkType.Vendor.ToDescription()))
                                message = "LastName is required";
                        }
                        break;
                    case "Company":
                        if (string.IsNullOrEmpty(Company) || (this.Company != null && this.Company.Trim().Length == 0))
                        {
                            if (Mark != null && Mark.Equals(MarkType.Vendor.ToDescription()))
                                message = "Company is required";
                        }
                        break;
                    case "Phone1":
                        if (string.IsNullOrEmpty(this.Phone1))
                            message = "Phone1 is required";
                        break;

                    case "Email":
                        if (string.IsNullOrEmpty(Email) || (this.Email != null && this.Email.Trim().Length == 0))
                        {
                            message = "Email is required";
                        }
                        break;
                    case "Website":
                        if (!string.IsNullOrWhiteSpace(Website))
                        {
                            string pattern = @"([\w-]+\.)+[\w-]+(/[\w- ./?%&=]*)?";
                            System.Text.RegularExpressions.Regex reg = new System.Text.RegularExpressions.Regex(pattern);

                            if (Website.Trim().Length > 100)
                                message = "Website is smaller 100 characters";
                            else if (!reg.IsMatch(Website))
                                message = "Website is not match format";
                        }
                        break;
                }

                if (!string.IsNullOrWhiteSpace(message))
                {
                    this.ExtensionErrors.Add(columnName);
                    return message;
                }

                return null;
            }
        }

        #endregion
    }
