        public int GetOnHandFromStore(int storeCode)
        {
            int onHandStore = 0;

            // Get property name
            string propertyName = CPC.Helper.Common.GetPropertyName(() => this.OnHandStore);

            // Get OnHandStore name
            string onHandStoreName = propertyName + (storeCode + 1);

            // Get OnHandStore property
            System.Reflection.PropertyInfo onHandStoreProperty = this.GetType().GetProperty(onHandStoreName);

            // Get OnHandStore value
            string onHandStoreValue = onHandStoreProperty.GetValue(this, null).ToString();

            // Convert on hand quantity to integer datatype
            int.TryParse(onHandStoreValue, out onHandStore);

            return onHandStore;
        }

        public void SetOnHandToStore(int quantity, int storeCode)
        {
            // Get property name
            string propertyName = CPC.Helper.Common.GetPropertyName(() => this.OnHandStore);

            // Get OnHandStore name
            string onHandStoreName = propertyName + (storeCode + 1);

            // Get OnHandStore property
            System.Reflection.PropertyInfo onHandStoreProperty = this.GetType().GetProperty(onHandStoreName);

            // Set OnHandStore value
            onHandStoreProperty.SetValue(this, quantity, null);
        }

        /// <summary>
        /// Update quantity on hand
        /// </summary>
        /// <param name="productModel"></param>
        public void UpdateQuantityOnHand(int numberOfStores)
        {
            int quantityOnHand = 0;

            for (int i = 0; i < numberOfStores; i++)
            {
                // Get OnHandStore value
                quantityOnHand += this.GetOnHandFromStore(i);
            }

            // Update QuantityOnHand
            this.QuantityOnHand = quantityOnHand;
        }

