        /// <summary>
        /// Calculator margin percent
        /// </summary>
        private void CalcMarginPercent()
        {
            if (RegularPrice > 0)
            {
                _marginPercent = Math.Round(100 * (1 - AverageUnitCost / RegularPrice), 2);
                OnPropertyChanged(() => MarginPercent);
            }
        }

        /// <summary>
        /// Calculator markup percent
        /// </summary>
        private void CalcMarkupPercent()
        {
            if (AverageUnitCost > 0)
            {
                _markupPercent = Math.Round(100 * (RegularPrice / AverageUnitCost - 1), 2);
                OnPropertyChanged(() => MarkupPercent);
            }
        }

