        #region IDataErrorInfo Members

        public string Error
        {
            get
            {
                List<string> errors = new List<string>();
                PropertyDescriptorCollection props = TypeDescriptor.GetProperties(this);
                foreach (PropertyDescriptor prop in props)
                {
                    string msg = this[prop.Name];
                    if (!string.IsNullOrWhiteSpace(msg))
                    {
                        errors.Add(msg);
                    }
                }
                return string.Join(Environment.NewLine, errors);
            }
        }

        public string this[string columnName]
        {
            get
            {
                string message = string.Empty;

                switch (columnName)
                {
                    case "ProductDepartmentId":
                        if (ProductDepartmentId == 0)
                            message = "ProductDepartment is required";
                        break;
                    case "ProductCategoryId":
                        if (ProductCategoryId == 0)
                            message = "ProductCategory is required";
                        break;
                    case "ProductName":
                        if (string.IsNullOrWhiteSpace(ProductName))
                            message = "ProductName is required";
                        break;
                    case "VendorId":
                        if (VendorId == 0)
                            message = "VendorID is required";
                        break;
                    case "BaseUOMId":
                        if (BaseUOMId == 0)
                            message = "BaseUOMId is required";
                        break;
                    case "WarrantyNumber":
                        if (WarrantyPeriod > 0)
                            if (!WarrantyNumber.HasValue || WarrantyNumber.Value == 0)
                                message = "WarrantyNumber is required";
                        break;
                }

                if (!string.IsNullOrWhiteSpace(message))
                    return message;
                return null;
            }
        }

        #endregion
